import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Game = () => {
    const [questionData, setQuestionData] = useState(null);  // Holds the question and solution data from API
    const [userGuess, setUserGuess] = useState('');          // Holds the player's current guess
    const [feedback, setFeedback] = useState('');            // Holds feedback for player (e.g., correct or incorrect guess)
    const [loading, setLoading] = useState(true);            // Tracks if the question is still loading

    // Fetches a new question from the Banana Game API when the component loads
    useEffect(() => {
        fetchQuestion();
    }, []);

    // Function to fetch question and solution data from the Banana Game API
    const fetchQuestion = async () => {
        setLoading(true); // Set loading to true before starting the fetch
        try {
            console.log("Fetching new question from API...");
            const response = await axios.get('/uob/banana/api.php?out=json');  // Use relative URL due to proxy
            console.log("API response:", response.data); // Log the API response

            if (response.data && response.data.question && response.data.solution) {
                setQuestionData(response.data); // Store API response in state
                setFeedback('');  // Clear feedback message on new question load
            } else {
                console.warn("API response does not contain question and solution data.");
                setFeedback('Error: Could not load game question.');
            }
        } catch (error) {
            console.error("Error fetching the question:", error);
            setFeedback('Failed to load game question. Please try again.');
        } finally {
            setLoading(false); // Set loading to false once the fetch completes
        }
    };

    // Function to handle the player's guess
    const handleGuess = () => {
        if (userGuess === questionData.solution) {
            setFeedback('Correct! Well done!'); // Provide positive feedback
            fetchQuestion();      // Fetch a new question if the guess is correct
            setUserGuess('');     // Clear the input field
        } else {
            setFeedback('Incorrect, try again!'); // Provide feedback to try again
        }
    };

    return (
        <div>
            <h1>Banana Game</h1>
            {loading ? (
                <p>Loading question...</p>  // Display a loading message while fetching the question
            ) : questionData ? (
                <div>
                    <p>Guess the number shown in the image:</p>
                    <img src={questionData.question} alt="Banana Game Question" />
                    <input
                        type="text"
                        value={userGuess}
                        onChange={(e) => setUserGuess(e.target.value)}
                        placeholder="Enter your guess"
                    />
                    <button onClick={handleGuess}>Submit Guess</button>
                    <p>{feedback}</p>
                </div>
            ) : (
                <p>{feedback || 'Error loading game question'}</p>  // Show error if no question data
            )}
        </div>
    );
};

export default Game;
