import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';  // For navigating between pages

const Levels = () => {
    // State to store the current level (this can be stored in localStorage if needed)
    const [currentLevel, setCurrentLevel] = useState(1);

    // Create an array of 5 levels with their names and unlocked status
    const levels = [
        { level: 1, name: 'Level 1', unlocked: true },
        { level: 2, name: 'Level 2', unlocked: currentLevel >= 2 },
        { level: 3, name: 'Level 3', unlocked: currentLevel >= 3 },
        { level: 4, name: 'Level 4', unlocked: currentLevel >= 4 },
        { level: 5, name: 'Level 5', unlocked: currentLevel >= 5 },
    ];

    // Use React Router's useHistory hook to navigate to the game page
    const history = useHistory();

    // Function to handle level click (navigates to the level's game page)
    const handleLevelClick = (level) => {
        if (level.unlocked) {
            // Navigate to the game's page for the selected level (game page logic is based on level)
            history.push(`/game?level=${level.level}`);
        } else {
            alert('This level is locked. Complete the previous levels first!');
        }
    };

    useEffect(() => {
        // Optionally, you could load this data from localStorage if you want persistent state
        const storedLevel = localStorage.getItem('currentLevel');
        if (storedLevel) {
            setCurrentLevel(Number(storedLevel));  // Set the current level based on the saved value
        }
    }, []);

    return (
        <div>
            <h1>Banana Game Levels</h1>
            <p>Choose your level to play!</p>
            <div>
                {levels.map((level, index) => (
                    <div key={index} style={{ margin: '10px 0' }}>
                        <button
                            style={{
                                padding: '10px',
                                backgroundColor: level.unlocked ? '#4CAF50' : '#ccc',
                                color: level.unlocked ? 'white' : 'gray',
                                cursor: level.unlocked ? 'pointer' : 'not-allowed',
                                borderRadius: '5px',
                                width: '100%',
                            }}
                            onClick={() => handleLevelClick(level)}
                            disabled={!level.unlocked}
                        >
                            {level.name}
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Levels;
