import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Game = () => {
    const [questionData, setQuestionData] = useState(null); // Holds question and solution data from the API
    const [userGuess, setUserGuess] = useState(''); // Holds player's current guess
    const [feedback, setFeedback] = useState(''); // Feedback for the player
    const [loading, setLoading] = useState(true); // Loading state for question fetch
    const [score, setScore] = useState(0); // Player's current score
    const [level, setLevel] = useState(1); // Current game level
    const [remainingAttempts, setRemainingAttempts] = useState(5); // Track attempts for each level

    // Define score thresholds for each level
    const scoreThresholds = [0, 20, 40, 60, 80, 100];
    // Define attempts per level to increase difficulty as levels progress
    const attemptsPerLevel = [5, 4, 3, 2, 1];

    useEffect(() => {
        fetchQuestion();
    }, [level]); // Fetch a new question when the level changes

    // Function to fetch question and solution data from the Banana Game API
    const fetchQuestion = async () => {
        setLoading(true); // Start loading before the fetch
        try {
            console.log("Fetching new question from API...");
            const response = await axios.get('/uob/banana/api.php?out=json'); // Use relative URL due to proxy
            console.log("API response:", response.data); // Log the API response

            if (response.data && response.data.question && response.data.solution) {
                setQuestionData(response.data); // Store question data in state
                setFeedback(''); // Clear previous feedback
                setRemainingAttempts(attemptsPerLevel[level - 1]); // Set attempts for the current level
            } else {
                console.warn("API response missing question and solution data.");
                setFeedback('Error: Could not load game question.');
            }
        } catch (error) {
            console.error("Error fetching the question:", error);
            setFeedback('Failed to load game question. Please try again.');
        } finally {
            setLoading(false); // End loading after fetch completes
        }
    };

    // Function to handle player's guess
    const handleGuess = () => {
        if (userGuess === questionData.solution) {
            setFeedback('Correct! Well done!');
            const newScore = score + 10; // Increase score for correct answers
            setScore(newScore);

            // Check if the score meets the threshold for the next level
            if (newScore >= scoreThresholds[level]) {
                setLevel(level + 1); // Advance to the next level
            } else {
                fetchQuestion(); // Fetch a new question within the same level
            }

            setUserGuess(''); // Clear input field for the next question
        } else {
            setFeedback('Incorrect, try again!');
            setRemainingAttempts(prev => prev - 1); // Decrease remaining attempts

            // Check if out of attempts
            if (remainingAttempts - 1 <= 0) {
                setFeedback('Out of attempts! Restarting level.');
                const penaltyScore = score - 5 >= 0 ? score - 5 : 0; // Penalize 5 points, but don’t go below 0
                setScore(penaltyScore);
                fetchQuestion(); // Restart level
            }
        }
    };

    return (
        <div>
            <h1>Banana Game</h1>
            <p>Score: {score} | Level: {level} | Remaining Attempts: {remainingAttempts}</p>
            {loading ? (
                <p>Loading question...</p>
            ) : questionData ? (
                <div>
                    <p>Guess the number shown in the image:</p>
                    <img src={questionData.question} alt="Banana Game Question" />
                    <input
                        type="text"
                        value={userGuess}
                        onChange={(e) => setUserGuess(e.target.value)}
                        placeholder="Enter your guess"
                    />
                    <button onClick={handleGuess}>Submit Guess</button>
                    <p>{feedback}</p>
                </div>
            ) : (
                <p>{feedback || 'Error loading game question'}</p>
            )}
        </div>
    );
};

export default Game;
